import React,{useEffect, useRef} from 'react';
import map from "./map.svg";
import {Map, ImageOverlay} from 'leaflet';

export default function MapGraphique() {

  const mapRef = useRef(null);

  useEffect(()=>{
    const mapInstance = new Map (mapRef.current, {
      crs: L.CRS.Simple,
      minZoom: -2,
      maxZoom: 3,
      zoomSnap: 0.1,
      zoomDelta: 0.1,
      zoomControl: false
    });
    const bounds = [[0,0, [1000,1000]]]; 
    const imageOverlay = new ImageOverlay(map, bounds); 
    imageOverlay.addTo(mapInstance); 

    mapInstance.fitBounds(bounds);
  }, []);
  return (
    <div ref={mapRef} style={{height: '500px'}}></div>
  );
}
