import React from 'react';
import {ComposableMap, Geographies, Geography} from "react-simple-maps";
import * as topojson from 'topojson-client';

const geoUrl =
  "https://raw.githubusercontent.com/deldersveld/topojson/master/continents/asia.json";

const mapConfig = {
  center: [120, 25],
  scale: 600,
  projection: "geoMercator"
};

const grayStyle = {
    default: { fill: "#E2E4E7", stroke: "#FFFFFF", strokeWidth: 0.5 },
    hover: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 },
    pressed: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 }
};
const chinaAndSouthKorea = ["China", "South Korea"];

export default function MapGraphique() {
    return (
      <ComposableMap {...mapConfig}>
        <Geographies geography={geoUrl}>
          {({ geographies }) =>
            geographies.map((geo) => (
              <Geography
                key={geo.rsmKey}
                geography={geo}
                style={{
                  default: {
                    fill: chinaAndSouthKorea.includes(geo.properties.NAME) ? (geo.properties.NAME === 'China' ? "#2196f3" : (geo.properties.NAME === 'South Korea' ? "red" : "#E2E4E7")) : "#E2E4E7",
                    stroke: "#FFFFFF",
                    strokeWidth: 0.5
                  },
                  hover: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 },
                  pressed: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 }
                }}
              />
            ))
          }
        </Geographies>
      </ComposableMap>
    );
  }
