import React from 'react';
import {ComposableMap, Geographies, Geography} from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/deldersveld/topojson/master/continents/asia.json";

const mapConfig = {
  center: [120, 25],
  scale: 600,
  projection: "geoMercator"
};

const grayStyle = {
    default: { fill: "#E2E4E7", stroke: "#FFFFFF", strokeWidth: 0.5 },
    hover: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 },
    pressed: { fill: "#182954", stroke: "#FFFFFF", strokeWidth: 0.5 }
};


export default function MapGraphique() {
  return (
    <ComposableMap {...mapConfig}>
      <Geographies geography={geoUrl}>
        {({ geographies }) =>
          geographies
            .filter(geo => selectedCountries.includes(geo.properties.NAME))
            .map((geo) => (
              <Geography
                key={geo.rsmKey}
                geography={geo}
                style={grayStyle.default}
              />
            ))
        }
      </Geographies>
    </ComposableMap>
  );
}
